sys_script.script:
(function executeRule(current, previous) {
    var control = new GlideRecord('sn_compliance_control');
    control.addQuery('content', current.getUniqueValue());
    control.addQuery('instance', true);
    control.addQuery('state', '!=', 'retired');
    control.orderBy("sys_created_on");
    control.query();
    var isSmartAssessmentPluginInstalled = false; //new sn_grc.GRCUtils().isSmartAssessmentPluginInstalled;
    while (control.next()) {
        control.name = current.name + '';
        control.description = current.description + '';
        control.reference = current.reference + '';
        control.category = current.category + '';
        control.type = current.type + '';
        control.classification = current.classification + '';
        if (control.requirement_level_attestation == false)
            control.attestation = current.attestation + '';
        control.supplemental_guidance = current.supplemental_guidance + '';

        if (isSmartAssessmentPluginInstalled) {
            control.assessment_method = current.assessment_method + '';
            control.smart_assessment = current.smart_assessment + '';
        }
        // control.state = 'draft';
        control.update();
    }
})(current, previous);
