sys_script_include.script:
var RiskUtilsAjaxBase = Class.create();
RiskUtilsAjaxBase.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    manageCurrencyFields: function() {
		var risk_sys_id = this.getParameter('risk_sys_id');
		gs.addInfoMessage('id: ' + risk_sys_id);
		var grRisk = new GlideRecord('sn_risk_risk');
		var entityTable = '';
		var hideCurrencyFields = false;
		grRisk.get(risk_sys_id);
					gs.addInfoMessage(grRisk.profile);

		if(grRisk) {
			entityTable = grRisk.profile.table;
			addInfoMessage('table: ' + entityTable);
			switch (entityTable) {
				case 'cmdb_ci_business_app':
					hideCurrencyFields = true;
					break;
				default:
					hideCurrencyFields = false;
			}
		}
		gs.addInfoMessage('result: ' + JSON.stringify(hideCurrencyFields));
		return JSON.stringify(hideCurrencyFields());
	},

	type: 'RiskUtilsAjaxBase'
});
