sys_script_include.script:
var AnalyticsFunctions = Class.create();
AnalyticsFunctions.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	limitSysIds: function() {
		var ids=[];
		var grProfile = new GlideRecord('sn_grc_profile');
		grProfile.addQuery('compliance_score', '>', 0);
		grProfile.orderBy('sys_compliance_scoreid');
		grProfile.setLimit(100);
		grProfile.query();
		while (grProfile.next()) {
			ids.push (grProfile.sys_id.toString());
		}
		return ids;
	},
    type: 'AnalyticsFunctions'
});
