sys_script_include.script:
var grcIssueAjaxUtilBase = Class.create();
grcIssueAjaxUtilBase.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    isCriticalControl: function()
	{
		var controlObjective = this.getParameter('controlObjective');

		var grCritical = new GlideRecord('x_631631_sc_grc_co_criticalcontrols');
		grCritical.addQuery('critical_control_objective',controlObjective);
		grCritical.query();
		if(grCritical.hasNext())
		{
		return true;			
		}
		else
		{
			return false;
		}

	},


	type: 'grcIssueAjaxUtilBase'
});
