var ThingUtils = Class.create();
ThingUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {



	getThing: function() {
var request = new sn_ws.RESTMessageV2();
request.setEndpoint('https://jsonplaceholder.typicode.com/todos/1');
request.setHttpMethod('GET'); //mention here POST or GET

request.setRequestHeader("Accept","application/json");
request.setRequestHeader('Content-Type','application/json');

var response = request.execute(); //It will send the post request to thord party tool

var responseBody = response.getBody();

// var responseBody = '{"userId" : 1, "id": 1, "title": "thing", "completed": false}';
// return JSON.stringify(responseBody);
return responseBody;
	},

putThing: function() {

	var requestObj = {
		"userId": 567,
		"id": 567,
		"title": "thing",
		"completed": false
	};
var request = new sn_ws.RESTMessageV2();
request.setEndpoint('https://jsonplaceholder.typicode.com/todos/1');
request.setHttpMethod('GET'); //mention here POST or GET

request.setRequestHeader("Accept","application/json");
request.setRequestHeader('Content-Type','application/json');

request.setRequestBody(JSON.stringify(requestObj));

request.execute();

var responseBody = response.getBody();
var status = response.getStatusCode();

return status;

},


	type: 'ThingUtils'
});
