sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var hdrs = {};
	var writer = response.getStreamWriter();
	var result = {};
		hdrs['Content-Type'] = 'application/json';
		response.setStatus(200);
		response.setHeaders(hdrs);

		var grSupplier = new GlideRecord('x_631631_grc_contr_supplier');
		grSupplier.setLimit(100);
		grSupplier.query();

		writer.writeString("{\"results\":[");

		while (grSupplier.next())
		{
			var supplierObj = {};
			supplierObj.name = grSupplier.supplier_name + '';
			supplierObj.id = grSupplier.system_id + '';
			writer.writeString(global.JSON.stringify(supplierObj));

			if(grSupplier.hasNext())
			{
				writer.writeString(",");
			}

		}
		writer.writeString("]}");
// 	}


})(request, response);
