sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var queryParams = request.queryParams;
	gs.info('qp len: ' + queryParams.supplier_name.length);
	for(var i=0;i<queryParams.length;i++)
	{
		gs.info('queryParams:' + queryParams.supplier_name[i] );
	}
	var hdrs = {};
	var writer = response.getStreamWriter();
	var result = {};
	hdrs['Content-Type'] = 'application/json';
	response.setStatus(200);
	response.setHeaders(hdrs);

	var grSupplier = new GlideRecord('x_631631_grc_contr_supplier');
	grSupplier.setLimit(100);
	grSupplier.add
	if(queryParams.supplier_name.length > 0)
	{
		var grOR = grSupplier.addQuery('supplier_name', 'CONTAINS', queryParams.supplier_name[0]);
		if(queryParams.supplier_name.length > 1)
		{
			for(i=1;i<queryParams.supplier_name.length;i++)
			{
				grOR.addOrCondition('supplier_name', 'CONTAINS', queryParams.supplier_name[i]);
			}
		}
	}
	grSupplier.query();

	writer.writeString("{\"results\":[");

	while (grSupplier.next())
	{
		var supplierObj = {};
		supplierObj.name = grSupplier.supplier_name + '';
		supplierObj.id = grSupplier.system_id + '';
		writer.writeString(global.JSON.stringify(supplierObj));

		if(grSupplier.hasNext())
		{
			writer.writeString(",");
		}

	}
	writer.writeString("]}");

})(request, response);
